-- GET_ONE
SELECT seq_no ,name_1 ,name_2 ,zip_code_1 || '-' || zip_code_2 AS 郵便番号 ,address_1 ,address_2 ,CASE WHEN tel_1 = '' THEN '' ELSE tel_1 || '-' || tel_2 || '-' || tel_3 END AS 電話番号 ,CASE WHEN fax_no_1 = '' THEN '' ELSE fax_no_1 || '-' || fax_no_2 || '-' || fax_no_3 END AS FAX番号 ,CASE WHEN client_type= '1' THEN '会社' ELSE '個人' END ASclient_type ,CASE WHEN has_nenga = true THEN '有' ELSE '' END AS has_nenga ,CASE WHEN has_reijo = true THEN '有' ELSE '' END AS has_reijo ,CASE WHEN has_gift = true THEN '有' ELSE '' END AS has_gift ,ochugen_name ,ochugen_price ,oseibo_name ,oseibo_price FROM m_clients WHERE seq_no = ?
